<template> <div class="selector-format-container"> <span class="selector-format-label" :style="labelStyle" >格式</span > <el-select-v2 class="selector-format-select" v-model="value" filterable clearable allow-create collapse-tags collapse-tags-tooltip :max-collapse-tags="maxCollapseTags" :options="data.options" placeholder="格式过滤" multiple ></el-select-v2> </div></template><script setup lang="ts"> type option = { value: string; label: string; }; interface IProps { value: any[]; options: option[]; fontSize?: string; maxCollapseTags?: number; } const props = withDefaults(defineProps<IProps>(), { value: () => [], options: () => <option[]>[], fontSize: "16px !important", maxCollapseTags: 1, }); const emits = defineEmits<{(e: "update", value: any[]): void}>(); const data = ref({ value: props.value, options: props.options, }); const value = computed({ get() { return data.value.value; }, set(val) { data.value.value = val; emits("update", val); }, }); const labelStyle = computed(() => { return { "font-size": props.fontSize, }; }); watch( () => props.value, (newVal, oldVal) => { data.value.value = newVal; } ); watch( () => props.options, (newVal, oldVal) => { data.value.options = newVal; } );</script><style lang="scss" scoped> .selector-format-container { position: relative; width: 100%; display: flex; flex-flow: row; justify-content: center; align-items: center; gap: 8px; .selector-format-label { margin: 0; text-align: center; white-space: nowrap; } .selector-format-select { flex-grow: 1; } }</style>