<template> <div class="container"> <span class="label">格式</span> <el-select-v2 class="select" v-model="value" filterable clearable allow-create collapse-tags collapse-tags-tooltip :max-collapse-tags="1" :options="data.options" placeholder="格式过滤" multiple ></el-select-v2> </div></template><script setup lang="ts"> type option = { value: string; label: string; }; interface IProps { value: []; options: option[]; } const props = withDefaults(defineProps<IProps>(), { value: () => [], options: () => <option[]>[], }); const emits = defineEmits<{(e: "update", value: []): void}>(); const data = ref({ value: props.value, options: props.options, }); const value = computed({ get() { return data.value.value; }, set(val) { data.value.value = val; emits("update", val); }, }); watch( () => props.options, (newVal, oldVal) => { data.value.options = newVal; } );</script><style lang="scss" scoped> .container { position: relative; width: 100%; display: flex; flex-flow: row; justify-content: center; align-items: center; gap: 8px; .label { margin: 0; text-align: center; white-space: nowrap; font-size: 16px !important; } .select { flex-grow: 1; } }</style>